import React, {Component} from 'react';

export default class Validation extends Component {
    state = {

    }

    checkInputLength = () => {
        let text = ''
        let {inputLength} = this.props;
        if (inputLength > 5) {
            text = 'Text long enough'
        } 
        return text = 'Text too short'
    }

    render () {
        console.log(this.props);
        return (
            <div>
                {this.checkInputLength}
            </div>
        );
    }
}
